def sort_books_pageCount (book_dict:object) -> list:
    """
    Written By: Calvin Bramble 101234882
    
    Returns the books, sorted by page count, and subsorted alphabetically by title
    
    >>>sort_books_pageCount(google_books)
    [{'title': 'Summary: Think and Grow Rich', 'author': 'Nine99 Innovation Lab', 'rating': '', 'publisher': 'Nine99 Innovation Lab (OPC) Pvt Ltd', 'page_count': 14, 'generes': 'Economics', 'language': 'English'},...]
    """
    booklist = dict_to_list_load_data(book_dict)
    
    #Sorts the list with a bubble sorting algorithm
    for i in range(len(booklist)):
        for j in range(len(booklist) - i - 1):
            #Sorts by page count
            if booklist[j].get("page_count") > booklist[j + 1].get("page_count"):
                booklist[j], booklist[j + 1] = booklist[j + 1], booklist[j]
            
            #Sorts alphabetically by title if 2 page counts are the same
            if booklist[j].get("page_count") == booklist[j + 1].get("page_count"):
                if booklist[j].get("title") > booklist[j + 1].get("title"):
                    booklist[j], booklist[j + 1] = booklist[j + 1], booklist[j]
                    
    for sortedbook in booklist:
        print(sortedbook)
        
    return booklist

